package JavaStudy.Chap_8;import java.io.*;import java.util.*;class Node {    Node next;    int data;    public Node(int data) {        next = null;        this.data = data;    }}class DNode extends Node {    DNode prev;    public DNode(int data) {        super(data);        prev = null;    }}class App {    Node head;    Node tail;    int size;    public App() {        head = null;        tail = null;        size = 0;    }    public App(int data) {        Node node = new Node(data);        head = node;        tail = head;        size = 1;    }    public void head(int data) {        Node node = new Node(data);        if (size < 2) {            head = node;            tail = head;        } else {            node.next = head.next;            head = node;        }    }}public class _08_ extends App {    public _08_() {}    public void printData(DNode node) {        System.out.println(node.data);    }    public DNode head() { return (DNode) super.head; }    public static void main(String[] args) {        _08_ temp = new _08_();        DNode a = new DNode(1);        DNode b = new DNode(2);        Node c = new Node(3);        temp.head(1);        temp.printData((DNode) c);    }}